mapper.select:
        SELECT


--         级联表的字段
        ,shuiguo.shangjia_id as shuiguoShangjiaId
        ,shuiguo.shuiguo_name as shuiguoName
        ,shuiguo.shuiguo_uuid_number as shuiguoUuidNumber
        ,shuiguo.shuiguo_photo as shuiguoPhoto
        ,shuiguo.shuiguo_types as shuiguoTypes
        ,shuiguo.shuiguo_erji_types as shuiguoErjiTypes
        ,shuiguo.tuijian_types as tuijianTypes
        ,shuiguo.tejia_types as tejiaTypes
        ,shuiguo.jinkou_types as jinkouTypes
        ,shuiguo.shuiguo_kucun_number as shuiguoKucunNumber
        ,shuiguo.shuiguo_old_money as shuiguoOldMoney
        ,shuiguo.shuiguo_new_money as shuiguoNewMoney
        ,shuiguo.shuiguo_clicknum as shuiguoClicknum
        ,shuiguo.shuiguo_content as shuiguoContent
        ,shuiguo.shangxia_types as shangxiaTypes
        ,shuiguo.shuiguo_delete as shuiguoDelete
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.new_money as newMoney

        FROM cart  a
        left JOIN shuiguo shuiguo ON a.shuiguo_id = shuiguo.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and (
                    a.shuiguo_id = #{params.shuiguoId}
                )


                  and a.buy_number >= #{params.buyNumberStart}   


                  and a.buy_number <= #{params.buyNumberEnd}   


                and a.buy_number = #{params.buyNumber}


                  and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


                  and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




                and a.shuiguo_id IS NOT NULL


                and shuiguo.shangjia_id = #{params.shangjiaId}


                and shuiguo.shuiguo_name like CONCAT('%',#{params.shuiguoName},'%')


                and shuiguo.shuiguo_uuid_number like CONCAT('%',#{params.shuiguoUuidNumber},'%')


                and shuiguo.shuiguo_types = #{params.shuiguoTypes}



                and shuiguo.shuiguo_erji_types = #{params.shuiguoErjiTypes}



                and shuiguo.tuijian_types = #{params.tuijianTypes}



                and shuiguo.tejia_types = #{params.tejiaTypes}



                and shuiguo.jinkou_types = #{params.jinkouTypes}



                  and shuiguo.shuiguo_kucun_number >= #{params.shuiguoKucunNumberStart}   


                  and shuiguo.shuiguo_kucun_number <= #{params.shuiguoKucunNumberEnd}   


                and shuiguo.shuiguo_kucun_number = #{params.shuiguoKucunNumber}


                  and shuiguo.shuiguo_old_money >= #{params.shuiguoOldMoneyStart}   


                  and shuiguo.shuiguo_old_money <= #{params.shuiguoOldMoneyEnd}   


                  and shuiguo.shuiguo_new_money >= #{params.shuiguoNewMoneyStart}   


                  and shuiguo.shuiguo_new_money <= #{params.shuiguoNewMoneyEnd}   


                  and shuiguo.shuiguo_clicknum >= #{params.shuiguoClicknumStart}   


                  and shuiguo.shuiguo_clicknum <= #{params.shuiguoClicknumEnd}   


                and shuiguo.shuiguo_clicknum = #{params.shuiguoClicknum}


                and shuiguo.shuiguo_content like CONCAT('%',#{params.shuiguoContent},'%')


                and shuiguo.shangxia_types = #{params.shangxiaTypes}



                  and shuiguo.shuiguo_delete >= #{params.shuiguoDeleteStart}   


                  and shuiguo.shuiguo_delete <= #{params.shuiguoDeleteEnd}   


                and shuiguo.shuiguo_delete = #{params.shuiguoDelete}



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   



        order by a.${params.orderBy} desc
